GtkWidget*
create_menuOpenDialer (void)
{
  GtkWidget *menuOpenDialer;
  GtkWidget *menuhelp;
  GtkWidget *menuquit;

  menuOpenDialer = gtk_menu_new ();

  menuhelp = gtk_menu_item_new_with_mnemonic (_("Help"));
  gtk_widget_show (menuhelp);
  gtk_container_add (GTK_CONTAINER (menuOpenDialer), menuhelp);

  menuquit = gtk_menu_item_new_with_mnemonic (_("Quit"));
  gtk_widget_show (menuquit);
  gtk_container_add (GTK_CONTAINER (menuOpenDialer), menuquit);

  g_signal_connect ((gpointer) menuhelp, "activate",
                    G_CALLBACK (on_menuhelp_activate),
                    NULL);
  g_signal_connect ((gpointer) menuquit, "activate",
                    G_CALLBACK (on_menuquit_activate),
                    NULL);

  /* Store pointers to all widgets, for use by lookup_widget(). */
  GLADE_HOOKUP_OBJECT_NO_REF (menuOpenDialer, menuOpenDialer, "menuOpenDialer");
  GLADE_HOOKUP_OBJECT (menuOpenDialer, menuhelp, "menuhelp");
  GLADE_HOOKUP_OBJECT (menuOpenDialer, menuquit, "menuquit");

  return menuOpenDialer;
}

