int InittextviewCodes(GtkWidget *window)
{
	
	GtkWidget *widget;
	widget = lookup_widget (window, "textviewCodes");
	PangoFontDescription *font_desc=NULL;

	font_desc=pango_font_description_from_string("-*-*bold-r-normal-*-120-*-*-*-*-iso8859-1");
	if(!font_desc)
	{
		//g_printf("pango_font_description_from_string failed\n");
		return 0;
	}
	pango_font_description_set_size(font_desc,19*PANGO_SCALE);
	gtk_widget_modify_font(widget,font_desc);
	pango_font_description_free(font_desc);
	return 1;
	
}
